changeSet.createProcedure:
  	create  trigger organisation_BI
    before insert on organisation_T
    for each row
		begin
		  if :new.organisation_ID is null
		  then
		    select SQ_organisation.nextval into :new.organisation_ID from dual;
		  end if;

		  /* Fill date and user created */
		  IF :NEW.created_on IS NULL
		  THEN
		      :NEW.created_on := SYSDATE;
		  END IF;

		  IF :NEW.created_by IS NULL
		  THEN
		      :NEW.created_by := UPPER(sys_context('USERENV', 'OS_USER'));
		  ELSE
		      :NEW.created_by := UPPER(:NEW.created_by);
		  END IF;
		end;

	  	create or replace trigger organisation_BU
    	before update on organisation_T
    	for each row
		begin
		    /* Fill date and user modified */
		    IF :NEW.modified_on IS NULL OR (:NEW.modified_on = :OLD.modified_on)
		    THEN
		        :NEW.modified_on := SYSDATE;
		    END IF;

		    IF :NEW.modified_by IS NULL OR (:NEW.modified_by = :OLD.modified_by)
		    THEN
		        :NEW.modified_by := UPPER(sys_context('USERENV', 'OS_USER'));
		    ELSE
		        :NEW.modified_by := UPPER(:NEW.modified_by);
		    END IF;

		end;

  	CREATE OR REPLACE FUNCTION organisation_BI()
		  RETURNS trigger AS
		 $BODY$
		begin

		  if NEW.organisation_id IS NULL 
			then
				begin
					NEW.organisation_id :=nextval('SQ_organisation');
				end;  
			end if;


		  IF NEW.created_on IS NULL
		  THEN
		      NEW.created_on := current_timestamp;
		  END IF;	

		  if NEW.created_by is NULL
		  THEN 
		    NEW.created_by :=UPPER(current_user);
		  ELSE
		  	NEW.created_by :=UPPER(NEW.created_by);  
		  END IF;  
		  RETURN NEW;	
		  EXCEPTION
		     WHEN OTHERS THEN 
		     RAISE WARNING 'organisation_BI: SQLSTATE %: %', SQLSTATE, SQLERRM;
		     RETURN NULL;  	
		end;$BODY$
		  LANGUAGE plpgsql VOLATILE
		  COST 100;

		CREATE TRIGGER organisation_BI BEFORE INSERT ON organisation_T
		    FOR EACH ROW EXECUTE PROCEDURE organisation_BI();

  	CREATE OR REPLACE FUNCTION organisation_BU()
  RETURNS trigger AS
 $BODY$
begin

  IF NEW.modified_on IS NULL or (OLD.modified_on = OLD.modified_on)
  THEN
      NEW.modified_on := current_timestamp;
  END IF;	

  if NEW.modified_by is NULL or (NEW.modified_by = OLD.modified_by)
  THEN 
    NEW.modified_by :=UPPER(current_user);
  ELSE
  	NEW.modified_by :=UPPER(NEW.modified_by);  
  END IF;  
  RETURN NEW;	
  EXCEPTION
     WHEN OTHERS THEN 
     RAISE WARNING 'organisation_BU: SQLSTATE %: %', SQLSTATE, SQLERRM;
     RETURN NULL;  	
end;$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

CREATE TRIGGER organisation_BU BEFORE UPDATE ON organisation_T
    FOR EACH ROW EXECUTE PROCEDURE organisation_BU();
